class Solution:
    def climbStairs(self, n: int) -> int:
        dp=[0 for i in range(n+1)]
        dp[0]=1
        dp[1]=1
        dp[2]=2
        def dfs(n):
            if dp[n]!=0:return dp[n]
            dp[n-2]=dfs(n-2)
            dp[n-1]=dfs(n-1)
            return dp[n-2]+dp[n-1]
        return dfs(n)
